import styled from "styled-components";
import { AnimatePresence, motion } from "framer-motion";

const Wrapper = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Box = styled(motion.div)`
  width: 200px;
  height: 130px;
  background-color: whitesmoke;
  opacity: 40%;
  border-radius: 10px;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1), 0 10px 20px rgba(0, 0, 0, 0.06);
  margin-left: 20px;
  margin-top: 20px;
`;

const Div = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
`


const Circle=styled(motion.div)`
  width: 40px;
  height:40px;
  background-color: rgba(255,255,255,1);
  border-radius: 50px;
  position: absolute;
  box-shadow: 0 2px 3px rgba(0,0,0,0.1), 0 10px 20px rgba(0,0,0,0.06);
`;

const hoverVariants={
  hover:{scale: 1.2}
}

const circleVariants={
  initial:{
    opacity: 0,
    scale: 0,
  },
  visible:{
    opacity: 1, 
    scale: 1,
    rotateZ: 360,
  },
  leaving:{
    opacity: 0,
    scale: 0,
    y: 50,
  }
}

function App() {
  const [showing, setShowing] = useState(false);
  const toggleShowing = ()=> setShowing((prev)=> ! prev);
  return (
    <Wrapper>
      <Div>
        <Box variants={hoverVariants}
        whileHover={"hover"} style={{originX:1, originY:1}}/>
        <Box />
      </Div>
      <Div>
        <Box />
        <Box variants={hoverVariants}
        whileHover={"hover"} style={{originX:0, originY:0}}/>
      </Div>
      <button>Switch</button>
      <AnimatePresence>
        {showing ? (
          <Circle
            variants={circleVariants}
            initial="initial"
            animate="visible"
            exit={"leaving"}
          />
        ): null}
      </AnimatePresence>

    <motion.div></motion.div>
  </Wrapper>
);
}

export default App;
